package adventofcodetwentytwentyfour;

import java.util.ArrayList;
import java.util.HashMap;

import utils.Util;

public class DayTwelve {
	static 	HashMap<String, String> set = new HashMap<String, String>();
	static 	ArrayList<String> output = new ArrayList<String>();
	static 	ArrayList<String> counterP = new ArrayList<String>();

	public static void main(String[] args) {
		// Integer Long String Float Double 
		String input = "";
		//String input = "RRRRIICCFFRRRRIICCCFVVRRRCCFFFVVRCCCJFFFVVVVCJJCFEVVIVCCJJEEVVIIICJJEEMIIIIIJJEEMIIISIJEEEMMMISSJEEE";
		String line = "GEGQQQQQQQQQQQQQPRRRWLLWWZIIIIIIIIIQQQQQNMNNNNNNNNNNNNNZZZZZZZZZZZZZZZZZZZZZZZZZZZOOOOOOOOHHHHHHHHHHHXKKKKTTTSSSNNNNNNIIIFIIIIIIIIIIILLSSSSG";
		//String line = "MMMISSJEEE";
		int len = line.length();
		for(int i =0; i < len; i++) {
			for(int j=0;j<len;j++) {
				set.put(j+","+i,""+input.charAt(i*len+j));
			}
		}
		ArrayList<ArrayList<String>> gardens = new ArrayList<ArrayList<String>>();
		
		for(String key : set.keySet()) {
			if(counterP.contains(key)) {continue;}
			output = new ArrayList<String>();
			gardens.add(recur(key, new int[] {-1,-1}, true));
		}
		
		
		long cost = 0;
		for(ArrayList<String> i : gardens) {
			long perimiter = 0;
			long area = 0;
			ArrayList<String> perimiterFromPoints = perimiterFromPoints(i);
			perimiter = discount(perimiterFromPoints);
			//System.out.println(perimiterFromPoints);
			//System.out.println(perimiterFromPoints.size());
			//System.out.println(discount(perimiterFromPoints));

			area = i.size();
			//System.out.println("area="+area+"\n");

			cost += perimiter*area;
		}
		
		System.out.println(cost);
	}
	
	public static int discount(ArrayList<String> array) {
		int index = 0;
		int count = 0;
		while(array.size()>0) {
			int[] curr = Util.Strings.strToIntarr(array.get(0)); // if there exists a point next to the one im at which shares its direction
			//System.out.println(array.indexOf((curr[0])+","+(curr[1])+","+curr[2]));
			//System.out.println(array);
			array.remove((curr[0])+","+(curr[1])+","+curr[2]);

			if(array.contains((curr[0]+1)+","+(curr[1])+","+curr[2])) {
				array = removeConnecting(array, index);
			}
			if(array.contains((curr[0]-1)+","+(curr[1])+","+curr[2])) {
				array = removeConnecting(array, index);
			}
			if(array.contains((curr[0])+","+(curr[1]+1)+","+curr[2])) {
				array = removeConnecting(array, index);
			}
			if(array.contains((curr[0])+","+(curr[1]-1)+","+curr[2])) {
				array = removeConnecting(array, index);
			}
			count++;
		}
		return count;
	}
	
	static ArrayList<String> connecting = new ArrayList<String>();
	public static ArrayList<String> removeConnecting(ArrayList<String> array, int index) {
		int[] curr = Util.Strings.strToIntarr(array.get(index)); // if there exists a point next to the one im at which shares its direction
		array.remove((curr[0])+","+(curr[1])+","+curr[2]);
		if(array.contains((curr[0]+1)+","+(curr[1])+","+curr[2])) {
			array = removeConnecting(array, index);
		}
		if(array.contains((curr[0]-1)+","+(curr[1])+","+curr[2])) {
			array = removeConnecting(array, index);
		}
		if(array.contains((curr[0])+","+(curr[1]+1)+","+curr[2])) {
			array = removeConnecting(array, index);
		}
		if(array.contains((curr[0])+","+(curr[1]-1)+","+curr[2])) {
			array = removeConnecting(array, index);
		}
		return array;
	}
	
	
	static 	ArrayList<String> existing = new ArrayList<String>();
	public static ArrayList<String> perimiterFromPoints(ArrayList<String> points) {
		ArrayList<Integer[]> P = new ArrayList<Integer[]>();
		for(String i : points) {
			P.add(new Integer[] {Util.Strings.strToIntarr(i)[0], Util.Strings.strToIntarr(i)[1]});
		}
		// P is the set of points in each garden
		// i is each point of the garden
		ArrayList<String> perm = new ArrayList<String>();
			for(Integer[] i : P) {
				if(points.contains((i[0]+1)+","+(i[1]))) {
				}
				else {
					perm.add((i[0]+1)+","+(i[1])+",0");
				}
				if(points.contains((i[0]-1)+","+(i[1]))) {
				}
				else {
					perm.add((i[0]-1)+","+(i[1])+",1");
				}
				if(points.contains((i[0])+","+(i[1]+1))) {
				}
				else {
					perm.add((i[0])+","+(i[1]+1)+",2");
				}
				if(points.contains((i[0])+","+(i[1]-1))) {
				}
				else {
					perm.add((i[0])+","+(i[1]-1)+",3");
				}
			}
			
		

		return perm;
	}
	
	public static ArrayList<String> recur(String str, int[] previous, boolean setting) {
		if(setting) {
			output.add(str);
			counterP.add(str);
		}
		int[] location = Util.Strings.strToIntarr(str);
		try {
			if(set.get(location[0]+","+location[1]).equals(set.get((location[0]+1)+","+location[1])) && output.contains((location[0]+1+","+(location[1]))) == false ) {
					recur((location[0]+1)+","+location[1], location, setting);
			}
		}
		catch (Exception e) {
		}
		try {
			if(set.get(location[0]+","+location[1]).equals(set.get((location[0]-1)+","+location[1])) && output.contains((location[0]-1+","+(location[1]))) == false) {
					recur((location[0]-1)+","+location[1], location, setting);
			}
		}
		catch (Exception e) {
		}
		try {
			if(set.get(location[0]+","+location[1]).equals(set.get(location[0]+","+(location[1]+1))) && output.contains((location[0]+","+(location[1]+1))) == false) {
					recur(location[0]+","+(location[1]+1), location, setting);
			}
		}
		catch (Exception e) {
		}
		try {
			if(set.get(location[0]+","+location[1]).equals(set.get(location[0]+","+(location[1]-1))) && output.contains((location[0]+","+(location[1]-1))) == false) {
					recur(location[0]+","+(location[1]-1), location, setting);
			}
		}
		catch (Exception e) {
		}
		
		return output;
	}
}