package alternatesolutionsaoctwentytwentyfour;
import java.util.ArrayList;
import java.util.HashMap;

import utils.Util;

public class DayTwelve {
	public static void main(String[] args) {
		HashMap<String, String> set = new HashMap<String, String>();

		String input = "";
		String line = "GEGQQQQQQQQQQQQQPRRRWLLWWZIIIIIIIIIQQQQQNMNNNNNNNNNNNNNZZZZZZZZZZZZZZZZZZZZZZZZZZZOOOOOOOOHHHHHHHHHHHXKKKKTTTSSSNNNNNNIIIFIIIIIIIIIIILLSSSSG";
		int len = line.length();
		for(int i =0; i < len; i++) {
			for(int j=0;j<len;j++) {
				set.put(j+","+i,""+input.charAt(i*len+j));
			}
		}
		ArrayList<ArrayList<String>> gardens = new ArrayList<ArrayList<String>>();
		
		for(String key : set.keySet()) {
			if(Util.Strings.twoDArrayListContains(gardens, key)) {continue;}
			gardens.add(Util.Maths.recursiveGrouping(set, key, new int[] {-1,-1}, new ArrayList<String>()));
		}
		
		long cost = 0;
		for(ArrayList<String> i : gardens) {
			//cost += Util.Maths.perimiterFromPoints(i).size()*i.size(); // partOne solution
			cost += Util.Maths.removeConnectingCounter(Util.Maths.perimiterFromPoints(i))*i.size(); // partTwo solution
		}
		
		System.out.println(cost);
	}
}